$today_weather = [];
$future_weather = [];
$weather = [];

if($data["success"] == "true"){
    $data = $data["records"]["locations"][0]["location"][0]['weatherElement'];

    //今日天氣資訊

    //星期幾
    array_push($today_weather, date('D',strtotime($data[0]['time'][0]['startTime'])));

    //月份
    array_push($today_weather, date('M',strtotime($data[0]['time'][0]['startTime'])));

    //日期
    array_push($today_weather, date('d',strtotime($data[0]['time'][0]['startTime']))."th");

    //天氣icon
    $rain = (int)((int)$data[0]['time'][0]['elementValue'][0]['value']/20);
    array_push($today_weather, $sun[$rain].$moon[$rain]);

    //溫度
    array_push($today_weather, ((int)$data[1]['time'][0]['elementValue'][0]['value']).'°');

    //降雨機率
    array_push($today_weather, (int)$data[0]['time'][0]['elementValue'][0]['value'].'%');

    //濕度
    array_push($today_weather, (int)$data[2]['time'][0]['elementValue'][0]['value'].'%');



    for($j=1; $j<7; $j++){
        
        $temp = [];
        
        $k=0;

        $i=$j*2;
        
        array_push($temp, strtoupper (date('D',strtotime($data[0]['time'][$i]['startTime']))));

        $rain = (int)((int)$data[0]['time'][$i]['elementValue'][0]['value']/20);
        array_push($temp, $sun[$rain].$moon[$rain]);

        array_push($temp, ((int)$data[1]['time'][$i]['elementValue'][0]['value']).'°');
        
        // echo "<br>";
        
        array_push($future_weather, $temp);

        
    }
    
    
    $weather["today"] = $today_weather;
    $weather["future"] = $future_weather;
    
    print_r($weather);
}else{
    echo "獲取天氣資料失敗";
}

